  `fhsetval` int(1) NOT NULL DEFAULT '0' COMMENT '已经设置',
  `sfjsset` int(1) NOT NULL DEFAULT '0' COMMENT '是否已经结算:0未结算,1:等待经销商确认。2:完成',
  `anzdizhi` varchar(255) DEFAULT NULL COMMENT '上面安装地址',
  `anzname` varchar(100) DEFAULT NULL COMMENT '安装人员姓名',
  `anztel` varchar(50) DEFAULT NULL COMMENT '安装人员联系电话',
  PRIMARY KEY (`id`),
  UNIQUE KEY `ordernum` (`ordernum`)
) ENGINE=InnoDB AUTO_INCREMENT=12 DEFAULT CHARSET=utf8 COMMENT='订单表';

